INSERT INTO `video` VALUES (1845, '王府软萌小刺客', '剧情：程序员洛薇薇意外进入剧本杀世界化身南疆国公主洛云曦！接到神秘任务：帮助北晟国沐王登上王位！洛云曦成功解决刺杀沐王的刺客后获得沐王宠爱；却被卷入一场南疆国与北晟国之间的惊天阴谋……', 'https://image.maimn.com/cover/c5121dbdbb240eb041a3468436e20b84.jpg', '中国', 2023, '电视剧', 15);
INSERT INTO `video` VALUES (1848, '直言真相第三季', '剧情：Apple TV+宣布，由奥斯卡奖得主Octavia Spencer主演和执行制片的NAACP获奖悬疑剧集《实话实说》将续订第三季。《实话实说》第三季由Nichelle Tramble Spellman创作和执行制片，Maisha Closson(曾出演《魔爪》和《逍遥法外》)将担任制片人和执行制片人。这部令人上瘾的电视剧的完整第一季和第二季现在正在Apple TV+上播放。', 'https://img.liangzipic.com/upload/vod/20230120-1/0981217e95dad29baa8e46738b5536a9.jpg', '美国', 2023, '电视剧', 9);
INSERT INTO `video` VALUES (1849, '梦中情人', '剧情：高峰一次意外被老婆赶去了客厅睡，就在当晚他做了一个梦。梦里他遇到了他梦寐以求的女孩，瞬间心动。可每次和女孩发展到关键时刻，总会醒来，因此高峰想到了一个妙招，就此故事展开。', 'https://image.smxjysm.com/cover/9cca8d684240e24dd459f2d439fae30c.jpg', '中国', 2023, '电视剧', 5);
