INSERT INTO `video` VALUES (1817, '一秒变总统第一季', '剧情：　　《一秒变总统》是一部由让-帕斯卡尔·扎迪和弗朗索瓦·欧容共同创作的喜剧。导演让-帕斯卡尔·扎迪饰演斯蒂芬·比利 — 一名无意间被卷入法国总统竞选的理想主义教育家。', 'https://img.liangzipic.com/upload/vod/20230121-1/962deea63e620a25d5e81b09c0d7ba5d.jpg', '', 2023, '电视剧', 6);
INSERT INTO `video` VALUES (1819, '去有风的地方红豆遥遥冰日常篇', '剧情：许红豆、谢之遥在云苗村从相遇到相爱的全过程，成年人恋爱的最好模样，还得看红豆遥遥冰暧昧推拉的高甜日常。', 'https://image.maimn.com/cover/b2ec3778d61b7fbba65a900aebc41c1a.jpg', '中国', 2023, '电视剧', 6);
INSERT INTO `video` VALUES (1821, '结婚了又如何', '剧情：主演·栁俊太郎，被隔了1年半的电视剧演出的今泉佑唯扮演的家庭的女人和山本千寻扮演的巴厘岛女子同时逼迫结婚，男人的真心话遗漏。', 'https://img.liangzipic.com/upload/vod/20220712-1/fcc259546aa219507401cd14a0a940c3.jpg', '日本', 2022, '电视剧', 2);
INSERT INTO `video` VALUES (1826, '通往内心的桥', '剧情：原作以儿童精神科诊所为舞台，讲述了自己也患有发育障碍（ADHD）的院长和实习医生的组合，面对各种各样的生活困难的孩子及其家人的故事。在电视剧中，山崎饰演了继承姑姑经营的小诊所的佐山卓，松本饰演了知道自己的发育障碍而放弃成为医生的实习医生远野志保。', 'https://img.liangzipic.com/upload/vod/20230121-1/a59a55f6b0f17004bf23798d3b9edb8a.jpg', '日本', 2023, '电视剧', 8);
INSERT INTO `video` VALUES (1827, '我不是特工', '剧情：　　【新戏选角】@陈翔橙 新戏#电视剧穿越黑暗到黎明#（又名《我不是特工》），10月27日开机，拍摄周期三个月，拍摄地横店，集数三十集。　　【主角人设】　　#陈翔#饰演的男主薛云烬是天蝎座，人称云少。云少是个重情守信之人，信奉的是以直报怨，这种性格的形成源自他幼年时那段颠沛流离的生活，父亲长年卧病在床，家财散尽后父子二人流落街头，以乞讨为生，尝尽人间冷暖的他开始形成了极端的性格，于他有恩者，云少不惜肝脑涂地，死而后已，于他有仇者，他不惮以最冷漠的手段予以反击。从小父亲就教导他，要向保卫自己生命一样信守诺言，云少记住了这句话，于是他的一生都桎梏于他的“诺言”。云少的一生，对三个人有所承诺，即马惠龙、段思绮、父亲，这也决定了云少的一生充满了对报恩的坚定，对情感的坚定，对信仰的坚定。', 'https://img.ffzypic.com/upload/vod/20230118-1/4f01b608b6b4f7b0c9523eedd0e8d76d.jpg', '中国', 2019, '电视剧', 42);
INSERT INTO `video` VALUES (1828, '逆袭之我家“斤”', '剧情：言情小说作家刘雨婷，微胖身材，好吃懒做，在找小说创作灵感时翻到一篇《肥婆女主也有春天》的文章时吐槽180斤肥婆不配和帅哥谈恋爱，因而被神秘系统惩罚穿书成为肥婆女主，只有完成攻略任务和男主莫如风在一起后才能回到原本的世界。一开始刘雨婷只是被动完成任务，后来因为心疼原主安雪晴的经历，决心虐一虐负心汉为其复仇。', 'http://lz.sinaimg.cn/large/8a65eec0gy1hag1h3xd7aj207i07iwep.jpg', '中国', 2023, '电视剧', 22);
