INSERT INTO `video` VALUES (2876, '伊普克雷斯档案第一季', '剧情：	　本剧改编自Len Deighton的同名间谍小说。Joe Cole饰演男主Harry Palmer，背景在60年代冷战时期，主角原本因故可能要入狱，但因为他当间谍的潜力被发挖出来，以及在柏林拥有人脉，故此他被迫进行危险的任务。', 'https://ugc.qpic.cn/newsapp_ls/0/14611425646/0', '英国', 2022, '电视剧', 6);
INSERT INTO `video` VALUES (2878, '超高跟', '剧情：	金荷娜在剧中饰演时尚推销员雨贤，虽然以亲民的形象保持着不错的业绩，但却对一切都停滞的状况很烦恼。\r\n\r\n	　　该剧将以雨贤为中心描写网络购物的现况。', 'https://ugc.qpic.cn/newsapp_ls/0/14611425836/0', '韩国', 2022, '电视剧', 14);
INSERT INTO `video` VALUES (2879, '孤国春秋第五季', '剧情：这一季对应原著小说第九部和第十部，主人公乌特雷德将夺回祖传领地——贝班堡。', 'https://ugc.qpic.cn/newsapp_ls/0/14611425897/0', '英国', 2022, '电视剧', 10);
INSERT INTO `video` VALUES (2883, '安迪·沃霍尔：时代日记', '剧情：该片由监制瑞恩·墨菲和导演安德鲁·罗西共同打造，共六集，以惊人的信息量描述了安迪·沃霍尔的传奇故事，这些在他去世后出版的日记为我们提供了独特窗口，带领我们深入了解这位艺术家非凡的一生。剧集开篇讲述了他在匹兹堡的童年，追溯了沃霍尔令人难以置信的多彩旅程，他以（既受人推崇又遭痛斥的）艺术家、导演、出版商、电视制片人、博眼球者、名人等身份，在不同媒介和不同时代之间自由穿梭。虽然沃霍尔经常暴露在镁光灯下，但他从不对外透露自己的个人生活。该剧使用尖端人工智能技术合成他的声音，通过他自己的语言，带观众认识这位极其复杂的人，并介绍了他身边的工作、创作和演出伙伴，这些人陪伴他从颠覆期到主流时期，其中包括约翰·沃特斯和罗伯·劳。《安迪·沃霍尔：时代日记》由墨菲、丹·博朗、乔希·布劳恩、斯戴西·里斯、斯坦利·布赫塔尔共同担任监制，罗西担任导演，该片绝妙地印证了沃霍尔的信...', 'https://ugc.qpic.cn/newsapp_ls/0/14611426217/0', '美国', 2022, '综艺', 6);
INSERT INTO `video` VALUES (2885, '粉雄救兵：德国篇', '剧情：《粉雄救兵：德国篇》是首部走向国际的美国热门真人秀节目。专家利尼·博尔特、大卫·雅各布斯、扬-亨利克·舍佩尔-施图克、艾友夏·穆塔尔迪和艾恩·尤鲁科引导学员们开启人生新篇章。该剧共五集，闪耀五人组帮助五名普通人进行积极改造，其中包括一位要出柜的年轻足球教练，一位重新开始约会的单身父亲，以及一位受挫的年轻女子慢慢学会享受生活。感人至深！', 'https://ugc.qpic.cn/newsapp_ls/0/14611426379/0', '', 2022, '综艺', 5);
INSERT INTO `video` VALUES (2908, '才不要和老板谈恋爱', '剧情：28岁的钱唯是个律师助理，顶头上司陆询是她的昔日同窗。钱唯认为陆询不念同学之情对她冷漠打压，皆因当年在校时冒犯过他，于是她一边怨天尤人，一边想着如何讨好陆询从而能摆脱职场困境。但在一次事故导致的昏迷中，钱唯梦到自己19岁的时光，意识到陆询对她的态度也许是另一种方式的关心。醒来的钱唯尝试走近陆询的内心世界，发现他正直且温暖的一面，也感受到他一直以来对自己的关爱。钱唯反思自己的生活态度，不再将困境委过于人，重拾法律人的初心。在和陆询携手打造法律援助平台、帮助当事人的过程中，钱唯战胜心结、成熟起来；两人也解开误会，共同成长，慢慢走到一起。经历种种考验后，钱唯和陆询收获了事业和爱情，有情人终成眷属。', 'http://lz.sinaimg.cn/large/006sgDEegy1h051l43iavj307i0b841v.jpg', '中国', 2022, '电视剧', 33);
INSERT INTO `video` VALUES (2909, '黑金风暴2022粤语', '剧情：重案组高级督察韦景声和香港廉政公署高级调查主任贺伟廉在缉凶过程中不期而遇，几经被赃款陷害、杀手追杀，搭档反目后，携手使一个几乎包含了整个香港顶层社会网络的贪腐势力逐渐浮出水面，据悉，该剧有九大案件贯穿始终，且部分案件是由真实事件改编 。', 'https://ugc.qpic.cn/newsapp_ls/0/14612949571/0', '中国', 2022, '电视剧', 30);
INSERT INTO `video` VALUES (2910, '黑金风暴2022国语', '剧情：重案组高级督察韦景声和香港廉政公署高级调查主任贺伟廉在缉凶过程中不期而遇，几经被赃款陷害、杀手追杀，搭档反目后，携手使一个几乎包含了整个香港顶层社会网络的贪腐势力逐渐浮出水面，据悉，该剧有九大案件贯穿始终，且部分案件是由真实事件改编 。', 'https://ugc.qpic.cn/newsapp_ls/0/14612949571/0', '中国', 2022, '电视剧', 30);
INSERT INTO `video` VALUES (2912, '金装的维尔梅～濒临堕落的魔法师和最强的灾厄一起冲入魔法世界～', '剧情：《幻想美食家》的原作”天那光汰“和《迷你无头》的作画”梅津叶子“联手创作的漫画《#金装的维尔梅#》宣布TV动画化~\r\n王立奥尔蒂亚魔法学院——。\r\n召唤魔术没有成功，濒临留级危机的学院生阿尔特，从偶然发现的古老召唤魔术书中描绘了魔法阵\r\n召唤被封印的恶魔维尔梅。她自古以来就是令人害怕的“恶魔”，拥有带来灾难的强大力量。\r\n必须定期向成为使魔的威尔梅提供魔力，那个方法是进行浓厚的接吻。\r\n对于这两人的关系感到焦虑的青梅竹马莉莉亚，以及对前所未闻的使魔感到愕然的学生们。\r\n与最强的使魔定下契约的ALTO，目标是成为伟大的魔法使“将魔导做到极致者白金广场”。\r\n悬崖边上的魔术师和不健康的恶魔姐姐的王道幻想开幕！', 'http://lz.sinaimg.cn/large/006sgDP3gy1h3koki5rwyj307i0agjsq.jpg', '日本', 2022, '动漫', 12);
INSERT INTO `video` VALUES (2913, '爱与生命之间', '剧情：这部带着黑色幽默的泛阿拉伯独立单元剧探索了普通人的爱和亲密关系', 'https://ugc.qpic.cn/newsapp_ls/0/14616113403/0', '', 2022, '电视剧', 8);
INSERT INTO `video` VALUES (2914, '托勒密·格雷最后的日子', '剧情：一个91岁的男子，被家人和朋友甚至他自己所遗忘，即将深深陷入孤独和痴呆。而在有机会短暂重获记忆时，格雷身上发生了巨大的转变，他要利用自己宝贵的清明时间去揭开侄子的死的谜题，以及接受自己的过去。', 'https://ugc.qpic.cn/newsapp_ls/0/14616113473/0', '美国', 2022, '电视剧', 6);
INSERT INTO `video` VALUES (2915, '上载新生第二季', '剧情：讲述“如果科技可以帮你拥有‘来生’”：在2033年，科技可以在人死去之时，把其意识和记忆上传到一个数字虚拟世界，也就是你可以在里面永远地“活着”，甚至能通过VR技术与真实人类社会的人接触。', 'https://ugc.qpic.cn/newsapp_ls/0/14616113570/0', '美国', 2022, '电视剧', 7);
INSERT INTO `video` VALUES (2917, '我的傻白甜媳妇2', '剧情：本剧讲述的是曾经的秦家少爷秦石川因遭奸人陷害被逐出秦家，为了生活在吕家做了个上门女婿，入赘三年，他受尽屈辱，是老婆家人、亲戚眼里的穷逼，废物。然而谁能想到华夏第一电商帝国大马哥是这个窝囊废的首徒！华夏第一互联网社交集团小马哥是这个窝囊废的二徒弟！华夏第一神医还是这个窝囊废的三徒弟！谁又能想到这个窝囊废竟然还是权可通天，富可敌国的燕京第一世家的大少爷……卷土重来的他又将掀起什么样的血雨腥风？', 'http://lz.sinaimg.cn/large/006sgDEegy1h067hmigdvj30780a4gm0.jpg', '中国', 2022, '电视剧', 26);
INSERT INTO `video` VALUES (2924, '对决', '剧情：在经济发达的粤东省，罗元县却是个治安垫底的县城，席卷而来的扫黑除恶时代洪潮，将其裹挟其中。年轻刑警武剑在追查一起枪击案的途中遇到黑恶势力的阻碍，交回警队的证据不翼而飞，他无法洗脱嫌疑，只有文陆阳毅然选择相信武剑，并邀请他配合专案组的工作。行事不按套路出牌的文陆阳与粗中有细的耿直小伙武剑自此组成一对风格迥异的破案组合，两人在互相试探中互相影响，从枪击案到光头被杀案，抽丝剥茧，挖出罗元县最大的黑社会性质组织团伙——陈氏兄弟集团，然而案件背后，还有一把巨大的保护伞，牵涉着更为庞大的势力，他们将如何冲破当地黑恶阻碍，还人民一片清明？', 'http://lz.sinaimg.cn/large/006sgDP3gy1h23kqnbpfej307i0b8t8r.jpg', '中国', 2022, '电视剧', 24);
INSERT INTO `video` VALUES (2927, '在荒丘上狩猎', '剧情：仓科担任主演的4月档电视剧，是以辻原登的同名小说为基础的《在寂寞的山丘上狩猎》。这是一部讲述因为男人们的自私欲望而身心受到伤害的两位女性，为了抓住希望而与男人们对抗的悬疑剧。', 'https://ugc.qpic.cn/newsapp_ls/0/14620150711/0', '日本', 2022, '电视剧', 8);
INSERT INTO `video` VALUES (2928, '一级方程式·疾速争胜第四季', '剧情：	每一个竞争激烈的赛季，不论赛道内外，一级方程式赛车选手、领队主管还有车队老板都过着赛车般分秒必争的快节奏生活。 剧集不光展现瞬息万变的赛道竞速，记录可能发生的车祸和意外比赛就像是一次次心脏病发作也将跟随车手了解他们在大赛前后的艰苦训练，讲述他们与家人和车队之间的故事。', 'https://ugc.qpic.cn/newsapp_ls/0/14620150749/0', '美国', 2022, '综艺', 10);
INSERT INTO `video` VALUES (2929, '小太郎一个人生活动画版', '剧情：叮咚！打开门，只见一个小男孩站在门口。\r\n　　他递来一盒精致的纸巾作为见面礼，并说道：“我是刚搬到 203 室的佐藤。请多关照。”这便是佐藤小太郎，他今年四岁，搬来清水公寓一个人生活。\r\n　　他将一把玩具佩剑别在腰上，进行每日的购物之旅。\r\n　　成熟又天真的小太郎，用他聪慧的方式感染着周围的人。\r\n　　这是关于一个四岁男孩的故事，他决定坚强起来，直到自己能和父母一起生活的那天到来。', 'http://lz.sinaimg.cn/large/006sgDEegy1h07f86ovorj307i0b341r.jpg', '日本', 2022, '动漫', 10);
INSERT INTO `video` VALUES (2933, '是王者啊？第一季', '剧情：王者荣耀日常番系列，由《王者荣耀》自制，选材轻松日常，呈现英雄们高光之外的生活小短片，2022年敬请期待~ 本季故事发生在王者大陆上塞外边关的长城脚下。为了守卫边关和平，每位登场的角色都远离故土，不远万里来到此地。虽然在这片陌生的土地上，他们都是“异乡人”，但共同的信念让他们有了一个共同的名字——长城守卫军。', 'http://lz.sinaimg.cn/large/006sgDEegy1h080q2bvtwj307i0aijva.jpg', '中国', 2022, '动漫', 14);
INSERT INTO `video` VALUES (2935, '真实身份', '剧情：在以染井为人的小说《真面目》为基础的本作品中，龟梨饰演的是作为某杀人事件的嫌疑犯被逮捕，被宣告死刑的镝木庆一。在移送途中越狱的他，一边逃跑一边把在潜伏地遇到的人们从穷境中救了出来。然后获救的人们在意识到镝木是通缉中的死刑犯的时候说：“他真的是杀人犯吗？”开始抱有疑问。', 'https://ugc.qpic.cn/newsapp_ls/0/14623222730/0', '日本', 2022, '电视剧', 4);
INSERT INTO `video` VALUES (2937, '万族之劫第二季', '剧情：苏宇成功考入大夏文明学府，但研究院的目标竟然与万族图册有惊人的相似！这里面有什么关联吗？少年意气风发，不断绽放着属于他的光彩。', 'http://lz.sinaimg.cn/large/006sgDEegy1h08b0gfykdj306808qdif.jpg', '中国', 2022, '动漫', 30);
INSERT INTO `video` VALUES (2938, '肮脏的黑袋子', '剧情：该剧讲述亚瑟·麦考伊(库珀饰)和臭名昭著、独来独往的赏金猎人红色比尔(布斯饰)为期8天的冲突展开。红色比尔以斩断受害者的头颅并将其塞入一个肮脏的黑袋子而闻名。该剧与经典的意大利西部片相呼应并向其致敬，讲述了赏金猎人、土匪和血腥的仇杀，以及被信仰、爱情和复仇等激情驱使的孤独灵魂。', 'https://ugc.qpic.cn/newsapp_ls/0/14623222458/0', '美国', 2022, '电视剧', 8);
INSERT INTO `video` VALUES (3069, '山河月明网络版', '剧情：	该剧讲述了明太祖朱元璋第四子朱棣从年少起就喋血沙场，历经战阵，终于成长为合格的军事统帅和成熟政治家，并成就一番伟业的故事。', 'http://lz.sinaimg.cn/large/006sgDEegy1h10d8c3gyrj307i0b9q5x.jpg', '中国', 2022, '电视剧', 45);
INSERT INTO `video` VALUES (3070, '熏制生活', '剧情：讲述的是26岁的咖啡店店长赖子（志田未来饰）和24岁的自由职业者巡（泉泽祐希饰）这对“同居进入第3个年头的情侣”，在两人唯一的共同休息日——星期天一起熏制食物，看看电影，玩玩游戏，品尝美食以养精蓄锐。两人感情也在“熏制”的过程中慢慢加深。', 'https://ugc.qpic.cn/newsapp_ls/0/14717191159/0', '日本', 2022, '电视剧', 13);
INSERT INTO `video` VALUES (3075, '绿色妈咪会', '剧情：该剧讲述了各自有着无法克服的烦恼，在论坛相遇的5个妈妈之间的友情，母性，以及成长的故事。', 'https://ugc.qpic.cn/newsapp_ls/0/14717191560/0', '韩国', 2022, '电视剧', 16);
INSERT INTO `video` VALUES (3076, '青山不墨', '剧情：上世纪50年代末，小兴安岭林区冬雪纷飞。青川林区和第一代林业工人迎来了严峻的考验。国家建设需要大幅度提高木材生产产量，能不能战胜困难完成几乎是不可能完成的生产指标？成为那一年和其后几年的主题。军人出身的林海市林业局局长郑毅对新任东方河林场的党总支书记魏建中的要求只有一句话：完不成生产任务提头来见。', 'https://ugc.qpic.cn/newsapp_ls/0/14717191588/0', '中国', 2022, '电视剧', 34);
INSERT INTO `video` VALUES (3078, '仙琦小姐许愿吧', '剧情：台湾神剧《家有仙妻》将重拍，由郭靖宇监制，原版导演伍宗德加盟，目前已开始筹备。在新版《家有仙妻》中，四位主要角色的姓名、职业都和老版不同，女主角陈仙琦是时尚杂志社的助理，男主角肖家佑则是初入职场的菜鸟律师，但延续了老版“仙镯奇缘”的设置。', 'http://lz.sinaimg.cn/large/006sgDEegy1h11iqk0v43j307i0b8tc3.jpg', '中国', 2022, '电视剧', 33);
INSERT INTO `video` VALUES (3107, '剑与远征-伊索米亚的那些事儿第一季', '剧情：改编自《剑与远征》游戏。《伊索米亚的那些事儿》首度爆笑来袭，讲述英雄们在伊索米亚大陆上发生的奇闻趣事。这里的英雄叽叽歪歪，不太正常！这里的冒险搞怪新奇，不讲武德！这里的生活鸡飞狗跳，绝不无聊！快跟着勇（sha）敢（diao）的英雄们，一起在伊索米亚的大陆上冒险吧！', 'http://lz.sinaimg.cn/large/006sgDEegy1h123359h8mj30ci0goail.jpg', '中国', 2022, '动漫', 12);
INSERT INTO `video` VALUES (3108, '苏小姐请留步', '剧情：将门嫡女苏九儿，因身姿飒爽身份尊贵，屡遭继母追杀暗害。她在一次逃亡中偶然邂逅了当朝九殿下，两人一见钟情，九殿下不顾婚约，将苏九儿娶进王府。这一行为使得恶毒继母与婚约原主苏玥变本加厉，设计陷害苏九儿，与此同时，当朝皇后又步步紧逼……一道圣旨，一场替嫁，看似是天赐良缘，可有情人能否眷属？', 'https://ugc.qpic.cn/newsapp_ls/0/14720994761/0', '中国', 2022, '电视剧', 12);
INSERT INTO `video` VALUES (3112, '美人危姬', '剧情：故事发生在1989年的墨西哥，32位来自全国各地的女孩来到一座神秘的庄园，为赢得“墨西哥小姐”选美比赛而努力训练。她们不仅需要赢得桂冠，更重要的是能活着结束比赛。', 'https://ugc.qpic.cn/newsapp_ls/0/14720994261/0', '', 2022, '电视剧', 8);
INSERT INTO `video` VALUES (3114, '欲望热线', '剧情：原著改编的电视节目. 在20 世纪80 年代的阿姆斯特丹，个性迥异的两兄弟开通了电话性爱热线。机缘巧合之下，一名雄心勃勃的大学生在此开启了新的职业生涯。', 'http://lz.sinaimg.cn/large/006sgDEegy1h12p85533mj307i0aidit.jpg', '', 2022, '电视剧', 6);
INSERT INTO `video` VALUES (3118, '名校风暴第五季', '剧情：Las Encinas 是西班牙最优秀、门槛最高的学校，也是精英阶层子女就读的去处。在地震震毁一所平民学校后，地方议会决定将学生们分至本地各校中，三个工薪家庭的孩子因此来到这所贵族学校。一无所有的穷孩子遇上应有尽有的富二代，激烈的冲突爆发，最终竟酿成谋杀。那么，罪魁祸首 到底是谁呢？', 'https://ugc.qpic.cn/newsapp_ls/0/14720994865/0', '', 2022, '电视剧', 8);
INSERT INTO `video` VALUES (3119, '青山遮不住', '剧情：一九七三年初，三线建设如火如荼进行中，北方某省的大山深处，代号“748”的小三线工厂扩建后，迎来了一批新职工。无论是外形样貌还是工作能力，风华正茂的路小青、左红卫、丁亚玲、李招弟、赵存根等人都是这批学徒中的佼佼者。在“748”工厂最辉煌的那几年里，几个年轻人经历了友情的考验、爱情的洗礼、工作的竞争、人生的迷茫。在长达几十年的峥嵘岁月里团结协助、追求梦想、改变自我，经历了情感纠葛、命运起伏、时代变迁，用激情和热血谱写了一曲荡气回肠的青春之歌。从青春年华到暮春之年，路小青把一生最好的时光和青春都献给了“748”厂，见证了它的辉煌、衰落、重生，一直到退出历史舞台。', 'https://ugc.qpic.cn/newsapp_ls/0/14717191588/0', '中国', 2021, '电视剧', 12);
INSERT INTO `video` VALUES (3122, '鲁比', '剧情：Ruby以Ruby Landry（Raechelle Banno）为中心，她出生在路易斯安那州的海湾，由她深爱的祖母凯瑟琳（Judd）照看。鲁比满怀希望，因为她与高中时的情人保罗·泰特（杜克饰）的爱情绽放，但她对神秘的父亲和母亲的去世的挥之不去的思念常常潜入鲁比的脑海。当保罗的父母禁止他与鲁比见面时，黑暗的家庭秘密开始显露出来，当她心爱的祖父去世时，鲁比更加伤心。鲁比被迫从河口逃到新奥尔良，她紧紧抓住保罗和他们被禁止的爱情的记忆，寻找她疏远的父亲（贝洛斯），他是这座城市最富有的人之一。', 'https://ugc.qpic.cn/newsapp_ls/0/14720994825/0', '美国', 2021, '电视剧', 1);
INSERT INTO `video` VALUES (3123, 'EXO的爬着梯子世界旅行第三季', '剧情：', 'https://ugc.qpic.cn/newsapp_ls/0/14720994995/0', '韩国', 2022, '综艺', 2);
INSERT INTO `video` VALUES (3124, '恋焰风暴', '剧情：', 'https://ugc.qpic.cn/newsapp_ls/0/14720995112/0', '泰国', 2022, '电视剧', 15);
INSERT INTO `video` VALUES (3125, '东京罪恶', '剧情：	　本剧松散改编自美国记者Jake Adelstein记录了东京警视厅辖区内各事件第一手资料的同名纪实文学。故事发生在90年代末，Jake Adelstein（Ansel Elgort饰）每天都会进入东京霓虹灯下的阴暗世界，在那里，一切都并非表面呈现的样子。而东京扫黑组的警探Hiroto Katagiri（渡边谦饰），对于Jake来说有着父亲榜样般的地位，他帮助Jake在警察和有组织犯罪世界之间那条纤细且往往不稳定的界线上前进。', 'https://ugc.qpic.cn/newsapp_ls/0/14720994872/0', '美国', 2022, '电视剧', 8);
INSERT INTO `video` VALUES (3126, '岳飞', '剧情：	公元1125年，金宋之间的战争全面爆发。在降官如毛、溃兵似潮的洪流中，岳飞选择了报效国家。凭着这个执着的信念，他开始打造一支保家卫国的劲旅岳家军。第一次北伐，岳飞夺回襄阳六郡，恢复故土在望，岳家军成为名符其实的中流砥柱。宋高宗准备把光复国土、中兴大宋的事业托付岳飞，岳飞迎来了生命中的黄金岁月。然而，宋高宗随即开始猜忌岳飞，虽然岳家军击破金军的优势兵力取得第四次北伐的胜利，却被十二道金牌诏书召回。不久后，岳飞被以莫须有的罪名杀害。英雄已去，精忠报国的精神却一直在激励着中华民族。', 'http://lz.sinaimg.cn/large/006sgDEegy1h12pa2wfguj307i0b9n0i.jpg', '中国', 2011, '综艺', 5);
INSERT INTO `video` VALUES (3127, '重返太空', '剧情：	SpaceX公司于5月27日发射了其首架载人航天飞机SpaceX之龙，独家摄像机从各个角度捕捉镜头，地面上的探索天才提供他们的专家分析，观众将有前所未有的机会体验自NASA退役航天飞机以来的首次载人航天飞行。', 'https://ugc.qpic.cn/newsapp_ls/0/14720995780/0', '美国', 2020, '综艺', 1);
INSERT INTO `video` VALUES (3135, '我们的蓝调', '剧情：该剧是卢熙京编剧和金圭泰导演继《那年冬天风在吹》《LIVE》后再次合作的作品，共20集，将描绘不同人的人生，本剧已于7月底开拍。讲述的是年轻时学生时代青涩的朋友们上了年纪再见面发生的故事，是一部颇具抒情色彩的作品。', 'http://lz.sinaimg.cn/large/006sgDEegy1h13vtm3ev6j307i0aldja.jpg', '韩国', 2022, '电视剧', 20);
INSERT INTO `video` VALUES (3137, '再次我的人生', '剧情：该片改编自同名小说，讲述人生第二次的热血检察官绝对的对恶的惩罚记。 一个雨夜，检察官金奚宇在与罪犯最后的对决中落败。跌入江水本该死去的他却被上天给予了重生一次的契机。再次醒来的他发现自己回到了高中时代，一场为了正义之名的复仇，展开了，这一次他能将位高权重的罪犯绳之以法吗？', 'http://lz.sinaimg.cn/large/006sgDEegy1h13vu2j9emj307i0aljtw.jpg', '韩国', 2022, '电视剧', 16);
INSERT INTO `video` VALUES (3138, '网购美食宅幸福', '剧情：改编中村明日美子与檟田尤利合作的漫画作品，以向井理饰演的抖S官能小说家檟村遥华和北村有起哉饰演的抖M漫画家中田实琉久为故事主轴。两人在相遇前就一直是彼此作品的粉丝，但实际见面后发现两人的长相和性格都与想像中不同，从他们共同的兴趣「宅配美食」而慢慢敞开心扉，让这两人超级完美☆的合作终于能有所发挥。', 'http://lz.sinaimg.cn/large/006sgDEegy1h13vvcywdwj307i060jtp.jpg', '日本', 2022, '电视剧', 12);
INSERT INTO `video` VALUES (3139, '猫物件', '剧情：“猫屋”的舞台是某家“带猫的合租屋”。不擅长与人交往的二星优斗，决定以祖父的死为契机开始share house，但因为爱猫，所以不能和“猫不认可的人”住在一起。优斗决定了以猫为中心的share house的规则，与追求梦想的人们开始了同居生活。', 'http://lz.sinaimg.cn/large/006sgDEegy1h13vuhxlq0j307i0amgp5.jpg', '日本', 2022, '电视剧', 10);
INSERT INTO `video` VALUES (3152, '我的出走日记', '剧情：三姐弟受够了日复一日毫无变化的成年生活，设法在平凡无奇的人生中寻找成就感与自由。', 'https://ugc.qpic.cn/newsapp_ls/0/14727840131/0', '韩国', 2022, '电视剧', 16);
INSERT INTO `video` VALUES (3153, '现在的年轻人啊', '剧情：	在一家名叫三桥商事的公司营业部里，有一位面相凶恶的课长，名为石泽一（反町隆史饰）。正在年轻社员们纷纷哀嚎怎么今天也要加班啊劳动方式改革根本就没有惠及我们啊之时，石泽向他们送去带着不满的眼神，你们啊，话音刚起，年轻社员们便迅速逃离了办公区。\r\n\r\n	其中，被分配到石泽手下的新社员麦田步（福原遥饰）怀揣着紧张和不安来到了公司。指导麦田的前辈叫舟木俊（中村海人饰），总是喜欢使用一堆谜一般的英语单词，搞得她完全跟不上工作。看着周围的同事个个工作得麻利又迅速，麦田感觉自己难以融入部门。对她来说，午休时间在单人桌吃便当，是唯一的轻松时刻。可就在这时，石泽突然出现在了麦田的面前，张口就来：你们现在这群年轻人啊。面对石泽的这句话，麦田将会如何回应呢？\r\n\r\n	石泽究竟是不是一个好上司呢？对于石泽那迷影重重的私生活抱有某种怀疑的麦田和年轻社员们，将会以何种态度与石泽这位上司相处呢？', 'https://ugc.qpic.cn/newsapp_ls/0/14727840176/0', '日本', 2022, '电视剧', 8);
INSERT INTO `video` VALUES (3154, '家裂第三季', '剧情：汉娜·斯特恩（Hannah Stern）做出了一个冒险的决定，离开她的家族公司，去参加一场关于离婚的激烈法律剧——在法庭内外。', 'https://ugc.qpic.cn/newsapp_ls/0/14727840229/0', '英国', 2022, '电视剧', 6);
INSERT INTO `video` VALUES (3158, '美味先生', '剧情：	潘是个勤奋的人。自从他父母去世后，他辍学开始工作来养活自己和他的小弟弟。潘先生在他租来的房子前开了一个露天货摊，但几乎入不敷出。那是在他的一个顾客招募他为他朋友拥有的一家酒店当厨师之前。酒店老板通(音)是一位年轻而头脑发热的商人。他们实际上以前见过面，他们对彼此的印象不好。当潘发现通是这家旅馆的老板时，他立即拒绝在那里工作。然而，通要求他做这项工作，他不想被人看不起，所以决定接受这个职位。通一尝就喜欢上了潘的菜，但又不想丢脸，就跟潘说菜难吃。这两个人见面总是打架。但是过了一段时间，他们每天的争吵发展成了一些特别的事情', 'https://ugc.qpic.cn/newsapp_ls/0/14727840437/0', '泰国', 2022, '电视剧', 8);
INSERT INTO `video` VALUES (3159, '猫咪合租屋', '剧情：	这是一部以全是帅哥的共享住宅的入住条件是喜欢猫咪的某个独栋房为舞台的究极治愈系猫片。片中描绘了爱猫且和猫一起生活的男人-二星优斗与逐梦的同居者们的人生姿态，还有通过猫咪来与人产生羁绊的方法，以及他们崭新的家族形式。饰演主人公?二星优斗的是，首次在其心心念念的猫咪电视剧中成为主演的古川雄辉。有意入住者们分别有：身为司法复读生的立花修（細田佳央太饰）、以演员为目标的岛袋毅（上村海成饰）、以职业拳击手为目标的矢泽丈（本田刚文饰）、来自中国台湾的留学生范（松大航也饰）。其他角色有四叶不动产的社员?广濑有美（长井短饰），优斗的祖父?二星幸三（龙雷太饰）', 'http://lz.sinaimg.cn/large/006sgDEegy1h1512iss9ij307i07iwep.jpg', '日本', 2022, '电视剧', 10);
INSERT INTO `video` VALUES (3160, '特搜9第五季', '剧情：	发生了警察被夺走自己的枪，后脑勺中弹死亡的事件。浅轮直树（井之原快彦饰）等特别搜查班开始展开搜查。逮捕了有前科的男子后，发生了第二起杀人事件。小宫山志保（羽田美智子饰）等人前往事件现场的餐厅，被杀害的是该店的厨师。得到了戴面具的男人的目击信息，不久，一名员工被拘留。然而，第三起事件又发生了。下一个被害者是从事黑钱行业的男子，作案手法和使用的手枪与第一、二起案件相同。监控摄像头拍到了那个戴面具的男人。直树灵光一闪，再次来到事件现场寻找线索。第四起事件终于发生了', 'http://lz.sinaimg.cn/large/006sgDEegy1h151329ljaj307i0alq5r.jpg', '日本', 2022, '电视剧', 12);
INSERT INTO `video` VALUES (3161, '黑人小姐速写喜剧第一季', '剧情：《黑人小姐速写喜剧》于8月2日开播，由该剧主创兼制片人罗宾·西德、阿什利·妮可·布莱克、加布里埃尔·丹尼斯和昆塔·布朗森等黑人女性主演。《不安感》中的伊萨·雷是EP，偶尔客串。', 'https://ugc.qpic.cn/newsapp_ls/0/14733927474/0', '美国', 2019, '电视剧', 2);
INSERT INTO `video` VALUES (3162, '家在顺德', '剧情：	系列纪录片《家在顺德》由中共佛山市顺德区委宣传部、佛山电视台顺德分台联合摄制，是全国第一部关注祠堂文化及家族传承的系列纪录片。全片分为聚、散、兴三集，每集45分钟，沿着家庭、家族、家乡、家国的脉络，着眼于记录顺德祠堂建筑与文化的传承与现状，以岭南区域文化为背景，呈现华夏文明底蕴、流变以及更加丰富多彩的现实景观，让祠堂之美被大众感知，岭南文脉得以传承，传递人们不断追求美好生活的正能量。', 'https://ugc.qpic.cn/newsapp_ls/0/14733927542/0', '中国', 2022, '综艺', 3);
INSERT INTO `video` VALUES (3163, '海洋奇境', '剧情：	太平洋是地球上最大、最深的海洋。从深海海沟到海底火山，太平洋涵盖了自然界所能提供的各种最极端的环境。太平洋上的岛屿，是比世界上任何其他地方都多，从长满椰树的环礁到贫瘠的火山岩礁石再到充满侏罗纪遗迹的沉没大陆架。太平洋最引人注目的是在它的生态系统中存在的生物，这些独特又离奇的生物已经适应了太平洋。本系列纪录片展现了许许多多以太平洋之家，在此生存、繁衍、进化的令人难以置信的生物。', 'https://ugc.qpic.cn/newsapp_ls/0/14733927627/0', '美国', 2022, '综艺', 2);
INSERT INTO `video` VALUES (3165, '最致命的动物', '剧情：	在《最致命的动物》系列中，每集以倒数的形式，向观众展示动物世界里最凶残的杀手、最诡秘的逃犯、最强悍的对手、最狡猾的罪犯。北美秃鹰在半空中为晚餐搏斗；猫鼬在族群中设有哨兵，以保证能在遇袭时全员逃离到安全地带；海狸与体重数倍于己的熊决斗，证明强悍与体型无关；强中自有强中手，谁是最致命的动物？', 'https://ugc.qpic.cn/newsapp_ls/0/14733927789/0', '美国', 2022, '综艺', 3);
INSERT INTO `video` VALUES (3166, '极致动物幼崽', '剧情：	或呆萌可爱，或可怜无助、或行为怪异、或毛骨悚然，从十倒数，世界上最极致凶残的动物幼崽。', 'https://ugc.qpic.cn/newsapp_ls/0/14733927871/0', '美国', 2022, '综艺', 6);
INSERT INTO `video` VALUES (3167, '木乃伊战士', '剧情：西奥多西亚·思罗克莫顿（Theodosia Throckmorton，又名西奥）是两位勇敢的埃及考古学家的14岁女儿，她于1906年在国王谷（Valley of the Kings）进行挖掘。当西奥和她的弟弟，发明狂亨利，偶然发现了一个隐藏的坟墓和一个神秘的神器，“荷鲁斯之眼”，西奥打开了看到和执行真正的魔法的能力，从而发现了一个充满神秘和危险的全新世界。现在，在她父母的家里，世界著名的传说和古物博物馆，在爱德华伦敦市中心，西奥必须与古老的力量、黑暗魔法和喜剧混乱作斗争，因为她和亨利，以及他们的朋友、街头魔术师威尔和埃及公主萨菲亚，试图在青少年生活中生存，通过教育和。。。拯救整个世界。', 'https://ugc.qpic.cn/newsapp_ls/0/14733927921/0', '美国', 2022, '电视剧', 13);
INSERT INTO `video` VALUES (3194, '亲爱的小孩2022', '剧情：小夫妻肖路与方一诺结婚多年，喜迎贵子。生产当日，产房外兵慌马乱的肖路却意外缺席。随着孩子的出生，这对新手爸妈也开始迎接身份转换后的各种挑战。随着双方父母的介入，一系列家庭矛盾频发，导致夫妻关系走向拐点与暗礁，两人最终分道扬镳。多年后，已各自重组家庭的他们生活归于平静，女儿禾禾却在此时被发现身患白血病。大夫从医学角度出发，建议他们再生一个孩子来拯救生命。于是，这场救女引发的风暴将几个家庭都推向人性的拷问和艰难的抉择。', 'http://lz.sinaimg.cn/large/006sgDEegy1h161ietkx0j307i0b9tbv.jpg', '中国', 2022, '电视剧', 34);
INSERT INTO `video` VALUES (3195, '这里禁止恋爱', '剧情：女扮男装进入男团，作为团宠成功出道，却被偶像前辈频频抓包，一段啼笑皆非的爱情故事由此展开。', 'https://ugc.qpic.cn/newsapp_ls/0/14733928077/0', '中国', 2022, '电视剧', 22);
INSERT INTO `video` VALUES (3200, '前男友的遗书', '剧情：绫濑遥确定主演悬疑剧《前男友的遗书》，改编自新川帆立的同名小说，将于今年4月开播。\r\n　　该剧讲述在大型律师事务所工作的干练律师剑持丽子(绫濑遥 饰)，得知前男友荣治去世的消息后，收到了他留下的奇怪遗言“我要把全部财产让与杀死我的犯人”，因而与委托人共同谋取巨额遗产。\r\n　　本剧由铃木雅之、泽田镰作担任导演，杉原宪明(《贞子》)执笔剧本。', 'https://ugc.qpic.cn/newsapp_ls/0/14737930458/0', '日本', 2022, '电视剧', 11);
INSERT INTO `video` VALUES (3203, '江南思雨', '剧情：	本剧讲述了时尚集团独女为挽救公司危机，与各方势力斗智斗勇的励志故事。', 'https://ugc.qpic.cn/newsapp_ls/0/14737930808/0', '中国', 2022, '电视剧', 30);
INSERT INTO `video` VALUES (3204, '绅士杰克第二季', '剧情：	　　故事来到1834年的约克郡。Anne Lister和Ann Walker以妻妻身份在希布登庄园安家，决定结合两人的财产，成为一对无所畏惧的伴侣，所有人的目光因此落在了她们身上。Anne Lister的企业家精神和她非传统的爱情生活，都让当地人感到惊恐。而哈利法克斯也正处在变革的边缘，她拒绝低调的态度让一切变得更容易激起争端，并引发危险。', 'https://ugc.qpic.cn/newsapp_ls/0/14737930908/0', '美国', 2022, '电视剧', 8);
INSERT INTO `video` VALUES (3205, '我的家', '剧情：鸣泽温人（二宫和也饰）是知名游戏公司CEO，与妻子未知留（多部未华子饰）、女儿友果（大岛美优饰）3人一起生活。有一天，结束工作回家的温人接到友果被绑架的电话，赎金要求5亿日元。警方立即成立了绑架事件的搜查总部，葛城圭史（玉木宏饰）等警察来到鸣泽家。无法马上准备5亿日元的温人，为了筹措现金，试图与某人物进行交涉。另一方面，未知留抱着抓住救命稻草的想法，与大学时代的朋友三轮碧（贺来贤人饰）和东堂树生（滨田岳饰）取得了联系。@哦撸马（阿点）', 'https://ugc.qpic.cn/newsapp_ls/0/14737931075/0', '日本', 2022, '电视剧', 10);
INSERT INTO `video` VALUES (3287, '大鱼（2003）', '剧情：在《大鱼》这部感人至深的电影里，导演蒂姆·波顿带我们进入了一对父子的内心世界。 　　\n　　威尔(比利·克鲁德普饰)记得，从小时候起，父亲爱德华(亚伯特·芬尼饰)就喜欢滔滔不绝的讲述自己早年当阿拉巴马旅行推销员时经历的众多离奇的故事，它们是如此的光怪陆离，就连小威尔也时常怀疑它们的真实性，他觉得自己根本就不了解父亲，也无法接受他的奇说，这让父子间的关系逐渐疏远。后来威尔和一名叫约瑟芬的法国女子结了婚，终于离开了自己的家和喋喋不休的父亲。 　　\n整整三年，威尔一直没和家里联系，这天母亲桑德拉(杰西卡·兰格饰)突然打来电话，她告诉威尔，他的父亲得了癌症，很快将不久人世，请他回家见父亲最后一面。威尔和妻子重返故里，威尔再次听到了父亲的那些他年轻时候的传奇故事。 　　\n根据爱德华的叙述，自己年轻时(伊万·麦奎格饰)是一位意气风发，野心勃勃的人，他一直想离开自己居住的小村庄，去体验一下外面大城市的生活。在旅行途中，他曾遇到过一个老巫婆，一个巨人，一个叫做“幽灵城”的地方，一个晚上会变成狼人的马戏', 'https://ugc.qpic.cn/newsapp_ls/0/14715914026/0', '美国', 2003, '电影', 1);
INSERT INTO `video` VALUES (3288, '最强神医混都市', '剧情：医道天才杨云帆在山上苦修多年后，下山寻找白富美未婚妻，却被视作乡巴佬打发了。令人想不到的是杨云帆看似普普通通，却身怀绝技，任何疑难杂症到他面前都手到病除，各种奇遇和危险迎刃而解。众人对他刮目相看，未婚妻以及各路美女投怀送抱，而他秉持着侠义当先的精神，救死扶伤，最终成为一代神医！', 'http://lz.sinaimg.cn/large/006sgDP3gy1h3gvcwwvxhj307i0a0wfl.jpg', '中国', 2022, '动漫', 45);
INSERT INTO `video` VALUES (3289, '唐伯虎点秋香粤语', '剧情：唐伯虎身为江南四大才子之首，却有道不尽的心酸。宁王想唐伯虎帮忙图谋作反，唐伯虎只好用内功改变脉象，令众人都相信他命不久矣。母亲埋怨他不应该把功夫暴露，因为他们家族一直被当年的仇人追杀。唐伯虎在与朋友出游时，遇到了貌若天仙的秋香并对她一见钟情，决心要到华府当家丁以追求秋香，唐伯虎被取名华安。期间华太师遇到了宁王上门刁难，幸好有唐伯虎出面相助，并暴露了自己是唐伯虎的身份。秋香才知道华安是自己欣赏的唐伯虎。华夫人跟唐家有怨，因此二人便开始斗法。怎料宁王跟夺面书生再次上门，华夫人不是对手，幸得唐伯虎出手，华夫人也答应把秋香许配给唐伯虎。', 'https://ugc.qpic.cn/newsapp_ls/0/14715917801/0', '香港', 1993, '电影', 1);
INSERT INTO `video` VALUES (3290, '野猪', '剧情：澳大利亚的乡村丛林中生活着一只野兽，它身躯庞大，性情残暴，破坏力极强。它无所畏惧，以武力捍卫自己的领地，以原始生猛的手段杀戮入侵者......', 'https://ugc.qpic.cn/newsapp_ls/0/14715919100/0', '澳大利亚', 2018, '电影', 1);
INSERT INTO `video` VALUES (3291, '天气之子（普通话）', '剧情：高一那年夏天，帆高离开位在离岛的家乡，独自一人来到东京，拮据的生活迫使他不得不找份工作，最后来到一间专门出版奇怪超自然刊物的出版社担任写手。不久，东京开始下起连日大雨，仿佛暗示着帆高不顺遂的未来，在这座繁忙城市里到处取材的帆高邂逅了与弟弟相依为命，不可思议的美少女阳菜。「等等就会放晴了喔。」阳菜这样告诉着帆高，不久，头顶的乌云逐渐散去，耀眼的阳光洒落街道……原来，阳菜拥有「改变天气」的奇妙能力……', 'https://ugc.qpic.cn/newsapp_ls/0/14715919275/0', '日本', 2019, '电影', 1);
INSERT INTO `video` VALUES (3292, '马达加斯加2：逃往非洲（普通话）', '剧情：金窝银窝，不如自家狗窝。终于“满足”了野外生存幻想，“脱线四人组”--狮子阿里克斯（本·斯蒂勒）、斑马马蒂（克里斯·洛克）、长颈鹿梅尔曼（大卫·施维默）和河马格罗莉娅（贾达·萍姬-史密斯），也开始怀念起了它们在纽约中央动物园里的“家”。马达加斯加再风光秀美，不过是个荒僻的世外桃源。丛林冒险的风吹雨打哪比得上饭来张口、衣来伸手的惬意日子？\n　　哪里有麻烦，哪里就有企鹅小分队“排忧解难”。胆大包天的小企鹅们，这次又有了锦囊妙计--只要能搞定小岛上那架破旧的失事飞机，不愁达不成这群娇生惯养的纽约客们回家享福', 'https://ugc.qpic.cn/newsapp_ls/0/14715919759/0', '美国', 2009, '电影', 1);
INSERT INTO `video` VALUES (3293, '神奇女侠1984（普通话）', '剧情：故事背景设定在五光十色、充满诱惑的80年代，神奇女侠戴安娜在华盛顿的自然历史博物馆过着与普通人无异的生活，然而在阻止了一场看似平常的劫案后，身边的一切都发生了变化。在强大的神力诱惑下，两位全新劲敌悄然出现——与神奇女侠“相爱相杀”的顶级掠食者豹女， 以及掌控着能改变世界力量的麦克斯·洛德，一场惊天大战在所难免。另外一边，旧爱史蒂夫突然“死而复生”，与戴安娜再续前缘，然而浪漫感动之余，史蒂夫的回归也疑窦丛生。\n　　新时代大幕开启，面对两位强大的邪恶反派和神秘归来的史蒂夫，神奇女侠要如何才能再次拯救世界？', 'https://ugc.qpic.cn/newsapp_ls/0/14715920176/0', '美国', 2020, '电影', 1);
INSERT INTO `video` VALUES (3294, '摩天营救（普通话）', '剧情：在香港市中心，世界上最高、结构最复杂的摩天大楼遭到破坏，危机一触即发。威尔·索耶（道恩·强森 饰）的妻子萨拉（内芙·坎贝尔 饰）和孩子们在98层被劫为人质，直接暴露在火线上。威尔，这位战争英雄、前联邦调查局救援队员，作为大楼的安保顾问，却被诬陷纵火和谋杀。他必须奋力营救家人，为自己洗脱罪名，关乎生死存亡的高空任务就此展开。', 'https://ugc.qpic.cn/newsapp_ls/0/14715920220/0', '美国', 2018, '电影', 1);
INSERT INTO `video` VALUES (3295, '笑傲江湖2：东方不败粤语', '剧情：令狐冲在按计划与日月神教的朋友会面后，将和小师妹岳灵珊等其他华山派师兄弟赴牛背山归隐退出江湖。路途中意外发现练就“葵花宝典”的东方不败，但令狐冲不认识东方不败，误将对方当成一位美貌女子。后来几次相遇，还一道饮酒吟诗。在同任盈盈、蓝凤凰会合后，几个月来江湖不平，大事连连。任我行为东方不败所囚禁，他们商量着夺回日月神教，便乔装进入浪人营打探，救出任我行。另一边华山派弟子惨遭毒手，黑木崖上，令狐冲和一干人都想着报仇，他惊觉眼前人竟是东方不败……', 'https://ugc.qpic.cn/newsapp_ls/0/14715921669/0', '中国', 1992, '电影', 1);
INSERT INTO `video` VALUES (3296, '雨夜惊魂', '剧情：故事讲述一个有“阴阳眼”的女孩扣子，在为男友庆生当夜，为了摆脱纠缠她的小女鬼而误伤了自己的男友，失去理智的她开车逃离男友家，却被突如其来的暴风雨困到一个雨水淹没的桥洞里。积水不断上涨，渗入车内，车门锁死，扣子打电话求救却不知身在何方，令她意想不到的是，小女鬼又来找她索命，绝望的她点燃了油箱……', 'https://ugc.qpic.cn/newsapp_ls/0/14715921843/0', '中国', 2015, '电影', 1);
INSERT INTO `video` VALUES (3297, '精灵鼠小弟', '剧情：9岁的乔治这天迎来了一个弟弟——小白鼠斯图亚特。这是他爸爸妈妈从孤儿院领回来的可爱精灵，会说话，有智慧，并且很希望有自己的家。然而，乔治却不喜欢斯图亚特，不愿意和他作伴。斯图亚特为此颇为犯愁。更令它处境不妙的是，它成为了肥猫眼中的佳肴，在虎视眈眈下过着冒险的生活。\n　　乔治参加了一个航模赛，但是他却一路遭到挫折，先是父亲没能帮他做好模型，当模型好不容易做成了，遥控器却在比赛那天被别人踩坏。在乔治每一次陷入困难的时候，正是斯图亚特机敏勇敢的出手相助，让乔治顺利过关。斯图亚特努力的付出，终于被小乔治接纳，这只小白鼠在乔治家中得到了温暖的归属。', 'https://ugc.qpic.cn/newsapp_ls/0/14715921847/0', '德国', 2000, '电影', 1);
INSERT INTO `video` VALUES (3298, '双子', '剧情：香港尖沙咀区内的一家廉价宾馆海洋宾馆这天来了一个少女（白田久子饰），她说自己叫霗，来找自己的卵生姐姐浅。老板昌叔夫妇听见后大吃一惊，但是包括其他住客在内，大家都一致否认见过中村浅。 \n　　但是霗来到宾馆的当晚，经营三流模特儿公司的住客阿龙突然暴毙，然后是昌叔夫妇、妓女苏丝、毒品拆家东尼，无一幸免。前来调查的探员政查出了浅死亡的真相——浅从日本到香港娱乐圈发展，到电视台试镜时以为遇上自己值得爱的男人东尼。谁知道东尼转身用一包白粉的价格把她卖给了别人。浅被强奸，与强奸的人打斗时，整整一个公寓的人，都冷眼旁观。没有一个人出言相劝，没有一个人出来阻止。政最终找到了她的尸体，但是从调查得来的资料看，浅是独生女，根本就没有什么孪生姐妹。 \n　　那，霗是谁？', 'https://ugc.qpic.cn/newsapp_ls/0/14715922266/0', '中国', 2005, '电影', 1);
INSERT INTO `video` VALUES (3299, '择日而亡（普通话）', '剧情：       一个特技女人和她的朋友们在意识到他们加入的那个团伙是一群诱饵的孩子之后，必须逃避一场血腥的战斗。\n　　这部电影有一个非常简单的情节，跟随主角克雷和她的一群朋友。缺钱，他们决定参加一场演出，这应该对他们来说是一笔现金。在出乎意料的情况下，他们被一群器官贩子缠住了，不得不在各种困难下度过一夜。', 'https://ugc.qpic.cn/newsapp_ls/0/14715922359/0', '', 2018, '电影', 1);
INSERT INTO `video` VALUES (3300, '拆弹专家粤语', '剧情：章在山（刘德华 饰）是香港警队“爆炸品处理科”的一名高级督察。七年前，他潜伏到头号通缉犯火爆（姜武 饰）的犯罪团伙中，在一次打劫金库的行动中，章在山表露了其拆弹组卧底的身份，与警方里应外合，成功阻止炸弹引爆，并将火爆及其弟的犯罪组织一网打尽，可惜在千钧一发之间，火爆逃脱并扬言誓要报仇。复职后的章在山很快被晋升为警队的拆弹专家。七年后，香港接二连三遭遇炸弹恐怖袭击，警方更收到线报大批爆炸品已偷运入港，一切迹象显示香港将有大案发生。就在香港人心惶惶之际，城中最繁忙的红磡海底隧道被悍匪围堵拦截，数百名人质被胁持，终于现身的火爆威胁警方炸毁隧道。章在山唯有将火爆绳之于法，才能拆解这场反恐风暴背后的惊天阴谋。', 'https://ugc.qpic.cn/newsapp_ls/0/14715922709/0', '香港', 2017, '电影', 1);
INSERT INTO `video` VALUES (3301, '黑色星期五6', '剧情：本片是由汤姆．麦克洛克林所导演的一部恐怖片，是黑色星期五的第六集。导演全片都用夜景拍摄，用背光和摄影机晃动，颇有恐怖气氛，此外还有不少幽默细节，观众在紧张时也能松弛一下大脑。露营杀手杰逊死了多年后，曾被他摧残的汤米从精神病院逃了出来，并到坟墓中挖出了杰逊的尸体来鞭尸报复，不料此时雷电交加，杰逊竟复活了。汤米向警方报案，反被拘捕，而杰逊再次到湖边向夏令营的儿童大肆杀戳。汤米从警察局逃了出来，跟杰逊展开了生死斗争。', 'https://ugc.qpic.cn/newsapp_ls/0/14715922732/0', '美国', 1986, '电影', 1);
INSERT INTO `video` VALUES (3302, '拯救2009', '剧情：圣诞节前夜，一群荷枪实弹的军人闯进了英国一条平静的死胡同，用枪逼迫居民回到家中，这里的居民毫无防备地卷入了一个暴力、恐怖、偏执的世界。单身妈妈绝望的想要保护对街的女儿。居民们很快发现，他们面对的危险远远超过任何想象，他们的性命堪忧。', 'https://ugc.qpic.cn/newsapp_ls/0/14715924041/0', '欧洲', 2009, '电影', 1);
INSERT INTO `video` VALUES (3303, '无间道长', '剧情：传说远古一段不为人知的时期，人类自命比天高，正气被遗弃，大地之神为警戒世人决定爆发一次大灾难，并引发起天崩地裂，被封锁于黑暗地下的妖魔亦乘机出动，在人间尽情破坏。就在这混乱昏暗之期，只有神剑门门主无间道人可以跟妖魔决一死战。因为无间道的灭魔圣剑能够灭魔斩妖，灭魔圣剑是神剑门先师以天外黑铁，倾毕生心血铸练而成，由于用剑者到最后都会人尽灯灭，故将剑与剑诀《天外飞仙》分开埋藏。童年时的三个好友：小错，小逆、小云，分别被道界魔界仙界收养，培养为无间，莫逆，圣女，三人命运一夕被改变，天下苍生命运的改变亦尽在其正邪一念间。为争夺剑与剑诀，魔界与道界展开了殊死搏斗', 'https://ugc.qpic.cn/newsapp_ls/0/14715919623/0', '香港', 2004, '电影', 1);
INSERT INTO `video` VALUES (3304, '黑暗天际', '剧情：美国一处理想中的郊外社区，贝瑞特一家生活于此。女主人莱丝美丽端庄，是一名干练的地产中介；男主人丹尼尔则正处在事业的瓶颈期，心中充满焦灼和不安。两个儿子杰西和山姆顽皮可爱，感情笃深。某个夜晚，贝瑞特家通往院子的拉门被打开，冰箱内的食物被人弄得乱七八糟。似乎从这一天起，种种异状便接连在这户人家出现。\n　　小山姆声称有外星人来到家中，但贝瑞特夫妇以及保全人员似乎却将家中一系列事件当作孩子的恶作剧。怪事愈演愈烈，远远超出孩子所能操控的范畴，真相究竟如何', 'https://ugc.qpic.cn/newsapp_ls/0/14715924202/0', '美国', 2013, '电影', 1);
INSERT INTO `video` VALUES (3305, '黑色星期五4', '剧情：在上集与女主角克里斯对决后，昏迷的杰森被误以为已死，随后被送去了医院的太平间。结果在苏醒之后，杰森杀掉护士并逃了出来，重新回到水晶湖营地大开杀戒。正值几名淫乱少年正在那里露营，经过一番新的洗礼，只有一个女孩和她的弟弟汤米幸存下来。最后的战斗中，二人合力将杰森斩杀。', 'https://ugc.qpic.cn/newsapp_ls/0/14715924218/0', '美国', 1984, '电影', 1);
INSERT INTO `video` VALUES (3306, '美版午夜凶铃（特别版）', '剧情：坊间流传着一个恐怖的传说，它和一盒神秘的录像带有关，传说中，凡是看过这盘录像带的人，会在七天之后死亡。理性至上的女记者凯勒只将此当成是一个玩笑，可侄女凯蒂的死却让她的信仰产生了动摇。凯勒的姐姐告诉凯勒，凯蒂曾和三个同学一起看过一盘诡异的录像带，而这三位同学无一例外都死于了非命。凯勒决定着手调查此事，她找到了凯蒂曾经看过的录像带并看了录像带中的内容，之后，一个神秘的电话预示了凯勒死期已近。想要活命，凯勒就必须在七天之内找到录像带诅咒的真相，时间紧迫，凯勒最终能够成功脱险吗？', 'https://ugc.qpic.cn/newsapp_ls/0/14715924490/0', '美国', 2002, '电影', 1);
INSERT INTO `video` VALUES (3307, '错爱粤语', '剧情：陆慧患上了胰脏癌，对自己、对同居男友恩明都是不小的打击。陆慧妹妹爱看鬼故事，在学校被老师批评。心情不好的陆慧看着不懂事的妹妹，脾气大坏。恩明一边抚恤陆慧的情绪，一边应付繁重的工作。遇上公司裁员的恩明在面试新工作时重遇小学同学朱芳婷，虽然恩明还是抽出一切工余时间陪伴陆慧，但是备受病魔折磨的陆慧强颜欢笑、情绪不稳，而且行为古怪，这些都给恩明带来了巨大的压力。朱芳婷的善解人意与轻松自然，让恩明不自觉靠近，二人发生了关系，而且瞒着陆慧一直继续下去。一个雨夜，陆慧失踪，恩明开车寻找的时候遇上车祸，突发奇想去芳婷家寻找，发现芳婷受了伤而且表情怪异。深夜回到家的陆慧浑身湿透，在接下来的日子里身体还会发出恶臭。恩明怀疑自己与陆慧是鬼，然而事情的发展超出了所有人的意料。', 'https://ugc.qpic.cn/newsapp_ls/0/14715914540/0', '中国', 2007, '电影', 1);
INSERT INTO `video` VALUES (3308, '绿野仙踪（普通话）', '剧情：小女孩桃乐丝被龙卷风吹到彩色的梦幻世界，而且把一个邪恶的女巫也给压死了。在善良的北方女巫的指点下，桃乐丝和小狗启程前往魔法师奥兹所居住的翡翠城，祈求他能帮助自己回家。路上，桃乐丝遇见了没有脑子的稻草人、缺少心脏的铁皮人以及胆小如鼠的狮子，为了达成愿望，他们随桃乐丝一同前行。一路上经历千难万险，更有邪恶的西方女巫处处阻挠，他们的愿望最终能否实现呢？', 'https://ugc.qpic.cn/newsapp_ls/0/14715924589/0', '美国', 1939, '电影', 1);
INSERT INTO `video` VALUES (3309, '黑色星期五5', '剧情：故事描述11年前，一个小男孩在水晶湖畔参加夏令营，不幸掉落湖中溺毙，该营地在此意外后便对外关闭了。直到11年后，一对年轻的夫妇决定在水晶湖畔，重新举办夏令营，并雇用了六名营队辅导员。当地居民纷纷警告他们，千万不要在水晶湖畔逗留。\n　　但是这对夫妇为了即将展开的夏令营活动，对居民的警告置之不理。没想到，怪异的事真的发生了！辅导员一个接一个莫名其妙的失踪。原本快乐的营地，变成了令人毛骨悚然的恐怖之地！似乎有个隐形杀手，暗地里阻止该营地的重新开张！', 'https://ugc.qpic.cn/newsapp_ls/0/14715924680/0', '美国', 1985, '电影', 1);
INSERT INTO `video` VALUES (3310, '银行家（普通话）', '剧情：20世纪50年代的美国，从小励志改变命运的黑人企业家伯纳德（安东尼·麦凯 饰）和乔（塞缪尔·杰克逊 饰）在种族制度的枷锁下，创业处处受阻。不得已他们想出奇招，雇佣白人马特（尼古拉斯·霍尔特 饰）来扮演他们产业帝国的“老大”，伯纳德和乔则扮成门房和司机，幕后操盘。就在他们的产业帝国越发壮大之时，却面临暴露的危险', 'https://ugc.qpic.cn/newsapp_ls/0/14715924959/0', '美国', 2020, '电影', 1);
INSERT INTO `video` VALUES (3311, '紧急突击队', '剧情：本片叙述一名企图使用核威胁的女主人公敲诈两千五百万美元的故事。美军绑架了一位塞尔维亚恐怖份子领袖，随即将送往国际法庭海牙受审，大为震怒的恐怖份子因而决定对美国采取报复行动关键问题是她本人从未离开过那座被原子弹威胁的城市。一般认为该威胁无法兑现。至于美国政府最终支付钱款，可被认为冲账行为。', 'https://ugc.qpic.cn/newsapp_ls/0/14715924990/0', '', 1999, '电影', 1);
INSERT INTO `video` VALUES (3312, '海滩的一天', '剧情：林佳莉（张艾嘉饰）成长于台湾南部传统小镇的医师家庭，她和哥哥林佳森（左鸣翔饰）二人自幼接受精英教育，从名校大学毕业。然而，佳莉不愿跟哥哥一样，被迫放弃恋人谭蔚青（胡因梦饰），而接受父亲之命所强加安排的婚姻。她毅然离家北上，和恋人程德伟（毛学维饰）在台北这个急速现代化的城市中结婚定居。 \n　　然而，70、80年代经济起飞的台湾，商业竞争骤烈，德伟逐渐卷入那个金钱竞逐、应酬酒色的世界，最终迷失了自己。愈益感到孤单空虚的佳莉，一日接到电话告知：德伟已经淹死在海边。她赶赴海滩边，面对那一具不知道究竟是不是丈夫的尸体。 \n　　多年后，佳莉重遇外国归来演出的谭蔚青，两人在咖啡厅回忆起旧事。', 'https://ugc.qpic.cn/newsapp_ls/0/14715904889/0', '台湾', 1983, '电影', 1);
INSERT INTO `video` VALUES (3313, '正义雇佣兵', '剧情：南非加莫拉岛拥有丰富的石油和钻石资源。在维吉尼亚州中情局总部一场政治阴谋正在计划着。德生是中情局的腐败份子，他以中情局的名义雇佣了查波的军团，表面上是帮助中情局去推翻驻军加莫拉岛军阀西比阿尔福卡的统治，而背地里德生是想要把加莫拉岛的资源占为己有。西格接到任务后带领着琼斯去前线与西比阿尔福卡军团战斗。但因情报有误，西格与琼斯遭到了法国突击队的围剿，琼斯牺牲了。西格请求援军但德生没有任何回应，他意识到被他们出卖了，便带着伤员逃离了战场。 \n　　西格回来后立即前往琼斯的家中看望了他的妻儿，留下了抚养费并向他的妻子保证定会给琼斯报仇。查波接到了新的任务，希腊军火商阿迈特戴森准备出大价钱来救出被关在监狱中的儿子。阿迈特戴森的儿子被关押在兰度监狱，这所监狱是南半球戒备最森严的监狱。查波觉得靠他现在的手下完成这次任务的话，几乎是不可能的，他便把西格请来，并让手下劫持了琼斯的妻儿强迫西格完成这次任务。西格对查波的雇佣兵并不信任，便要求与佣兵黑客塞繆尔一同执行这次任务……', 'https://ugc.qpic.cn/newsapp_ls/0/14715925057/0', '美国', 2006, '电影', 1);
INSERT INTO `video` VALUES (3314, '怒海沉尸', '剧情：汤姆（阿兰·德龙 Alain Delon 饰）是一个穷小子，最近，一桩好差事找上了门，好友菲利普（莫里斯·荣内特 Maurice Ronet 饰）的富豪父亲找到了汤姆，希望他能够替自己找回成日在外风流潇洒的儿子，作为回报，汤姆将得到价值不菲的报酬。 \n　　循着菲利普的踪迹，汤姆来到了拿布里，见到了菲利普和他的未婚妻玛奇（玛丽·拉福莱 Marie Laforêt 饰）。菲利普纸醉金迷的生活让汤姆十分羡慕，玛奇的美丽和温柔更是摄取了汤姆的心魄，一个邪恶的念头应运而生。一次出游中，汤姆设计杀死了菲利普，之后，为了掩盖罪行，汤姆又杀死了菲利普的好友。一个谎言，要用无数个谎言来掩盖，在得到了自己想要的一切后，汤姆终将面临审判。', 'https://ugc.qpic.cn/newsapp_ls/0/14715925998/0', '', 1960, '电影', 1);
INSERT INTO `video` VALUES (3315, '马格瑞姆的神奇玩具店', '剧情：对外号称自己243岁的马格瑞姆先生，开了一家有200年历史神奇的玩具店，每件玩具都有自己的生命力，莫莉·马奥尼是这家玩具店的经理，小时候的她因为能出色的弹奏“拉赫曼尼诺夫第二号钢琴协奏曲”而被认为是一个天才钢琴手，可是直到她22岁，却一直无法突破瓶颈，变的对自己自信心不足。老实刻板的会计师亨利·韦斯顿被雇佣为玩具店做资产评估，和玩具店中一直交不到朋友的小伙计埃里克渐渐成为了朋友。 突然有一天，马格瑞姆宣布要离开，要将店交给莫莉，莫莉却不相信自己有这样的能力去经营这玩具店，而此时，一直明亮欢乐的玩具店也开始变的阴暗而丧失色彩，难道马格瑞姆的玩具店真的要从此消失吗？，朋友们要如何帮助莫莉建立自己的信心呢？', 'https://ugc.qpic.cn/newsapp_ls/0/14715926154/0', '', 2007, '电影', 1);
INSERT INTO `video` VALUES (3316, '巫山历险记（普通话）', '剧情：许多年以来，美国的内华达大沙漠一直是个神秘的地方。有人说，这是因为外星人的飞船曾在巫山降落。杰克是一个拉斯维加斯的出租车司机，有一天他在车上发现了一对神秘的兄妹俩。从伸出援手的那一刻开始，他就卷入了一场令人惊心动魄的冒险旅程。原来，这对拥有异能的兄妹来自外星，且还隐藏了不为人知，却足以使整个宇宙毁灭的秘密。兄妹两人必须在现有的时间内找到失踪的太空船，返回外星。', 'https://ugc.qpic.cn/newsapp_ls/0/14715926301/0', '', 2009, '电影', 1);
INSERT INTO `video` VALUES (3317, '致命的危机', '剧情：正值花季年龄的少女尼科尔对爱情抱着不切实际的幻想。在一次狂欢晚会上她结识了很有魅力的男孩戴维.麦考尔。他的文雅、体贴和强健深深地迷住了她。但是时间一长，尼科尔发现了这个男孩花心的一面，他居然背着她与另一女孩玛戈厮混。从此，美梦开始破灭而恶梦接踵而至，全家人包括父亲、继母和继母带来的小弟弟，都面临着“致命的威胁”。', 'https://ugc.qpic.cn/newsapp_ls/0/14715926302/0', '', 1996, '电影', 1);
INSERT INTO `video` VALUES (3318, '开膛手杰克', '剧情：A serial killer walks the streets of Whitechapel, London, attacking and killing women, which later came to be known as the \'Whitechapel murders\' stretching from 3 April 1888 to 13 February 1891 included in the London Metropolitan Police Service Investigation.', 'https://ugc.qpic.cn/newsapp_ls/0/14715926645/0', '德国', 2016, '电影', 1);
INSERT INTO `video` VALUES (3319, '2014MAMA亚洲音乐盛典', '剧情：2014Mnet亚洲音乐大奖以2013年10月23日至2014年10月27日在韩国发行的专辑和音源为对象，由MAMA专业审查委员团评选出入围歌手及作品。最终TAEYANG凭借歌曲《眼，鼻，嘴》获得最佳年度歌曲奖，EXO获得最佳年度歌手奖，其专辑《上瘾》获得最佳年度专辑奖。', 'https://ugc.qpic.cn/newsapp_ls/0/14715927378/0', '日本', 2014, '综艺', 1);
INSERT INTO `video` VALUES (3320, '斯文江南', '剧情：全国首档经典文本围读节目《斯文江南》，邀请多位演艺名家/文化学者共同聚焦五大江南文化地标: 浙江绍兴、江苏苏州、上海、安徽宣城、浙江杭州。\n以历代文人雅士围绕该地域所创作的经典文学作品为核心，【强文本】通过围读作品，点燃文学内涵；【强实景】内景打造园林实景，演绎历史情境，抒发人物情怀；【强共鸣】致敬文化先贤，延展人生体悟。\n从实地走读、戏剧围读到全民共读，名家名作名山名楼，立足江南辐射中华，见贤思齐涵养道德，让古今现代的经典文本成为人们内心不朽的文化图腾。\n致敬飞奔的时代，聆听文学的乡音。', 'https://ugc.qpic.cn/newsapp_ls/0/14715927414/0', '中国', 2022, '综艺', 11);
INSERT INTO `video` VALUES (3321, '围炉音乐会2021', '剧情：2021年正逢建党100周年，《围炉音乐会》将推出“百年情，围炉歌”为主题的特别策划，节目将邀请建党百年历史里的建设者、参与者、见证者一起来到围炉音乐会现场，由优秀歌唱家、演唱者登上“围炉舞台”唱响经典的革命歌曲和正能量歌曲，传递不忘初心、矢志不移的时代强音。节目拟通过感人至深的时代故事配以慷慨豪迈的歌声...', 'https://ugc.qpic.cn/newsapp_ls/0/14715927512/0', '中国', 2021, '综艺', 4);
