INSERT INTO `video` VALUES (3333, '拜托了妈妈', '剧情：国内首档专业母婴养育互动节目。《拜托了妈妈》于3月1日强力登录天津卫视黄金时段，李静将放下主持人的身份，与瑶淼和每期一位明星妈咪共同组成“明星妈妈帮”现身说法。', 'https://ugc.qpic.cn/newsapp_ls/0/14715931778/0', '中国', 2016, '综艺', 13);
INSERT INTO `video` VALUES (3334, '美好的星城', '剧情：《美好的星城》是一档文化旅游探寻综艺节目，节目通过嘉宾旅行，深入走访当地的普通百姓，挖掘普通人身上的故事，感受当地独具特色的民俗和传承技艺。节目将致力于从普通人视角体验新文旅，推广“新国潮”，让广大观众在感受美好生活的同时，不断弘扬民族自信和文化自信。', 'https://ugc.qpic.cn/newsapp_ls/0/14715932103/0', '中国', 2021, '综艺', 10);
INSERT INTO `video` VALUES (3335, '第十九届“汉语桥”世界大学生中文比赛', '剧情：第十九届“汉语桥”世界大学生中文比赛，由教育部中外语言合作交流中心主办，央视频及央视创造传媒承办。比赛首次采用“云录制”，汇集五大洲选手，打造新媒体互动赛事。', 'https://ugc.qpic.cn/newsapp_ls/0/14715932345/0', '中国', 2020, '综艺', 89);
