INSERT INTO `video` VALUES (3381, 'NMB48', '剧情：NMB48（エヌエムビー フォーティエイト）是由秋元康担任总制作人，以近畿地区为中心活动的日本女性偶像团体。是女子偶像团体AKB48和SKE48的姐妹团体。在位于大阪难波的NMB剧场进行定期演出。', 'https://ugc.qpic.cn/newsapp_ls/0/14717186651/0', '日本', 2010, '综艺', 9);
INSERT INTO `video` VALUES (3382, '《师说》第一季', '剧情：师者，传道授业解惑也；道之所存，师之所存。师者，用学识与品德影响社会的一群人。《师说》是中欧于2018年重磅推出的系列专题片，以独特的视角，细腻的笔触，为你带来几位人生经历各不相同的中欧教授课堂之外精彩另一面。', 'https://ugc.qpic.cn/newsapp_ls/0/14717186815/0', '中国', 2018, '综艺', 2);
