INSERT INTO `video` VALUES (3384, '开“新”吧！2022', '剧情：京东开“新“吧2022！再见 emo君，跨年微综艺，让我们一期告别emo，开启崭新的2022！', 'https://ugc.qpic.cn/newsapp_ls/0/14717186931/0', '中国', 2021, '综艺', 1);
INSERT INTO `video` VALUES (3385, '桑东有缘第三季', '剧情：《桑东有缘》是一档自制的人物访谈节目，主要关注藏族艺术名人、藏区各行各业的领军人物以及在各自领域有突出贡献和引领能力的人物。', 'https://ugc.qpic.cn/newsapp_ls/0/14717186999/0', '中国', 2019, '综艺', 12);
INSERT INTO `video` VALUES (3386, '脱贫大决战第一季', '剧情：河南卫视着力打造的我国首档使命类大型公益扶贫节目《脱贫大决战》在兰考开机拍摄。据悉，这在国内开了“TV+扶贫”先河。同时，河南广电16位主持人将组成脱贫圆梦团，为贫困户和创业项目众筹。节目中邀请敬一丹、白岩松、濮存昕、邓亚萍、刘洋、刘震云、海霞、张泽群等众多公益名人加盟。据介绍，《脱贫大决战》将于10月26日起至12月28日，每周三21:15在河南卫视播出。', 'https://ugc.qpic.cn/newsapp_ls/0/14717187126/0', '中国', 2016, '综艺', 10);
INSERT INTO `video` VALUES (3387, '剧风行动：“德云社”十周年专访', '剧情：剧风行动：“德云社”十周年专访', 'https://ugc.qpic.cn/newsapp_ls/0/14717187269/0', '中国', 2021, '综艺', 42);
INSERT INTO `video` VALUES (3388, '丝路云裳·穿在身上的艺术第三季', '剧情：《丝路云裳——穿在身上的艺术》是云南卫视原创的大型民族服饰赛装文化季播节目，每期邀请2位国内著名服装设计师，通过艺术原乡采风、衣的远方对话、裳的故乡浸没式T台秀、文创主播团互动访谈等内容，致敬中华优秀传统文化，通过大小屏联动，聚焦中国民族服饰的创造性转化与创新性发展。', 'https://ugc.qpic.cn/newsapp_ls/0/14717187320/0', '中国', 2021, '综艺', 10);
INSERT INTO `video` VALUES (3389, '联想智能生态高校创新大赛', '剧情：11支热血团队会战《最强大脑》录制现场；“水哥”化身麻辣评审，犀利点评不得不服；“联想智能生态高校创新大赛”见证青春脑力狂飙！', 'https://ugc.qpic.cn/newsapp_ls/0/14717187340/0', '中国', 2018, '综艺', 1);
INSERT INTO `video` VALUES (3390, '建党百年·风华筝茂', '剧情：建党百年·风华筝茂古筝专场节目由孙艺霜担任总导演并执行，节目于2021年5月1日在北京演播厅录制，汇聚了全国上百个优秀的古筝节目，节目于6月在CCTV高清综艺娱乐频道上线播出。', 'https://ugc.qpic.cn/newsapp_ls/0/14717187337/0', '中国', 2021, '综艺', 6);
